import React, { useState } from "react"
import {Button} from './Button'
import {Link} from 'react-router-dom' //for webpage loading?
import './Navbar.css'
import Dropdown from './Dropdown'

function Navbar(){

    const [click, setClick] = useState(false); //click toggle for Hamburger
    const [dropdown, setDropdown] = useState(false);// toggle for dropdowns



    const handleClick = () => setClick(!click); 
    const closeMobileMenu  = () => setClick(false);

    const onMouseEnter = () =>{
        if(window.innerWidth<960){  //hides the dropdown if on mobile
            setDropdown(false);
        } else {
            setDropdown(true);
        }
    };
    const onMouseLeave = () =>{
        if(window.innerWidth<960){  //hides the dropdown if on mobile
            setDropdown(false);
        } else {
            setDropdown(false);
        }
    };
    return(
        <>
            <nav className="navbar">
                 <Link to='/' className='navbar-logo'> {/*place for main logo */}
                    MIL header?
                </Link>
                <div className="menu-icon" onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'}/> {/*Hamburger switching */}
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className="nav-item">
                        <Link to='/' className="nav-links" onClick={closeMobileMenu}>
                            Home
                        </Link>
                    </li>
                    <li className="nav-item"
                        onMouseEnter={onMouseEnter}
                        onMouseLeave={onMouseLeave}
                    >
                        <Link to='/services' className="nav-links" onClick={closeMobileMenu}>
                            Services <i className="fas fa-caret-down" />
                        </Link>
                        {dropdown && <Dropdown/>}
                    </li>
                    <li className="nav-item">
                        <Link to='/contact-us' className="nav-links" onClick={closeMobileMenu}>
                            Contact Us
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to='/sign-up' className="nav-links-mobile" onClick={closeMobileMenu}>
                            Sign up
                        </Link>
                    </li>
                </ul>
                <Button/>

            </nav>
        </>
    );
}

export default Navbar;
