import React, { useState, useEffect } from 'react';

const ImageCarousel = ({ imagePaths }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % imagePaths.length);
        setIsTransitioning(false);
      }, 500); // Adjust the time for the transition
    }, 5000); // Change the interval time as needed

    return () => clearInterval(interval);
  }, [imagePaths.length]);

  const goToNextSlide = () => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % imagePaths.length);
      setIsTransitioning(false);
    }, 500); // Adjust the time for the transition
  };

  const goToPrevSlide = () => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + imagePaths.length) % imagePaths.length);
      setIsTransitioning(false);
    }, 500); // Adjust the time for the transition
  };

  return (
    <div className="carousel">
      <button className="carouselButtons" onClick={goToPrevSlide}><i className='fas fa-caret-left' /></button>
      <div className="slide" style={{ transition: 'opacity 0.5s', opacity: isTransitioning ? 0 : 1 }}>
        <img src={process.env.PUBLIC_URL + imagePaths[currentIndex]} alt={`Slide ${currentIndex}`} />
      </div>
      <button className="carouselButtons" onClick={goToNextSlide}><i className='fas fa-caret-right' /></button>
    </div>
  );
};

export default ImageCarousel;
