import React, { useState, useEffect, useRef } from 'react';

const ImageCarousel = ({ imagePaths }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const nextIndexRef = useRef((currentIndex + 1) % imagePaths.length);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(nextIndexRef.current);
      nextIndexRef.current = (nextIndexRef.current + 1) % imagePaths.length;
    }, 5000); // Change the interval time as needed

    return () => clearInterval(interval);
  }, [imagePaths.length]);

  return (
    <div className="carousel">
      <div className="slide">
        <img
          src={process.env.PUBLIC_URL + imagePaths[currentIndex]}
          alt={`Slide ${currentIndex}`}
          className="current"
        />
        <img
          src={process.env.PUBLIC_URL + imagePaths[nextIndexRef.current]}
          alt={`Slide ${nextIndexRef.current}`}
          className="next"
        />
      </div>
    </div>
  );
};

export default ImageCarousel;
